import React, { useState } from 'react';
import product from './product';
console.log(product); // üîç v√©rifie dans la console si les variants apparaissent
import 'bootstrap/dist/css/bootstrap.min.css';
import { Card, Button } from 'react-bootstrap';

function App() {
  const [variantIndex, setVariantIndex] = useState(0);
  const [firstName] = useState("Phina"); 

  if (!product.variants || product.variants.length === 0) {
    return <p>Erreur : Aucune variante de produit trouv√©e.</p>;
  }

  const variant = product.variants[variantIndex];

  const nextVariant = () => {
    setVariantIndex((prev) => (prev + 1) % product.variants.length);
  };
console.log("VARIANT ACTUEL :", variant);

  return (
  <div className="container mt-5">
  <div className="row justify-content-center">
    <div className="col-md-6">
      <Card className="text-center p-4 shadow-lg bg-white">
        <Card.Body>
          <Card.Title className="mb-3 fs-3">{variant.name}</Card.Title>
          <Card.Img
            variant="top"
            src={variant.image}
            alt={variant.name}
            className="mx-auto d-block"
            style={{ width: "180px" }}
          />
          <Card.Text className="mt-3">{variant.description}</Card.Text>
          <pre>{JSON.stringify(variant, null, 2)}</pre>
          <h4 className="mt-3 text-success">{variant.price}</h4>
          <Button className="mt-3" variant="primary" onClick={nextVariant}>
            Changer de mode
          </Button>
        </Card.Body>
      </Card>

      <p className="text-center mt-4 fs-5">
        {firstName ? `Bonjour, ${firstName} !` : "Bonjour !"}
      </p>

      {firstName && (
        <div className="text-center">
          <img
            src="https://cdn-icons-png.flaticon.com/512/2922/2922561.png"
            alt="User"
            width="100"
            className="rounded-circle shadow-sm"
            style={{ marginTop: '10px' }}
          />
        </div>
      )}
    </div>
  </div>
</div>

  );
}

export default App;
