// src/App.js
import React from 'react';
import 'bootstrap/dist/css/bootstrap.min.css';
import { Card, Container } from 'react-bootstrap';
import { motion } from 'framer-motion'; // ðŸ‘‰ Import animation
import Name from './Name';
import Price from './Price';
import Description from './Description';
import Image from './Image';
import product from './product';

const firstName = "Phina"; // Changez ce prÃ©nom ou laissez une chaÃ®ne vide

function App() {
  const [imageIndex, setImageIndex] = useState(0);

  const nextImage = () => {
    setImageIndex((prevIndex) => (prevIndex + 1) % product.images.length);
  };
  return (
    <Container className="mt-5 d-flex justify-content-center">
      <motion.div
        initial={{ opacity: 0, y: -50 }}
        animate={{ opacity: 1, y: 0 }}
        transition={{ duration: 0.8 }}
      ></motion.div>
      <Card style={{ width: '22rem' }} className="shadow p-3">
        <Card.Body className="text-center">
          <Name name={product.name} />
          <Image image={product.image} name={product.name} />
          <Price price={product.price} />
          <Description description={product.description} />
        </Card.Body>
      </Card>

      <div className="text-center mt-3">
        {firstName ? (
          <>
            <h5>Bonjour, {firstName} !</h5>
            <img
              src="https://cdn-icons-png.flaticon.com/512/2922/2922510.png"
              alt="user"
              width="50"
            />
          </>
        ) : (
          <h5>Bonjour !</h5>
        )}
      </div>
    </Container>
  );
}

export default App;
