import React, { useState } from 'react';
import product from './product';
import 'bootstrap/dist/css/bootstrap.min.css';
import { Card, Button } from 'react-bootstrap';

function App() {
  const [variantIndex, setVariantIndex] = useState(0);
  const [firstName] = useState("Phina"); 

  if (!product.variants || product.variants.length === 0) {
    return <p>Erreur : Aucune variante de produit trouv√©e.</p>;
  }

  const variant = product.variants[variantIndex];

  const nextVariant = () => {
    setVariantIndex((prev) => (prev + 1) % product.variants.length);
  };

  return (
    <div className="container mt-4">
      <Card className="text-center p-4 shadow-lg">
        <Card.Body>
          <Card.Title>{variant.name}</Card.Title>
          <Card.Img
            variant="top"
            src={variant.image}
            alt={variant.name}
            style={{ width: "150px", margin: "10px auto" }}
          />
          <Card.Text>{variant.description}</Card.Text>
          <h4 className="mt-3">{product.price}</h4>
          <Button variant="primary" onClick={nextVariant}>Changer de mode</Button>
        </Card.Body>
      </Card>

      <p className="text-center mt-3">
        {firstName ? `Bonjour, ${firstName} !` : "Bonjour !"}
      </p>

      {firstName && (
        <div className="text-center">
          <img
            src="https://cdn-icons-png.flaticon.com/512/2922/2922561.png"
            alt="User"
            width="100"
            style={{ marginTop: '10px' }}
          />
        </div>
      )}
    </div>
  );
}

export default App;
