import React, { useState } from 'react';
import product from './product';
import 'bootstrap/dist/css/bootstrap.min.css';
import { Card, Button } from 'react-bootstrap';

function App() {
  const [variantIndex, setVariantIndex] = useState(0);
  const [firstName] = useState("Phina"); 

  if (!product.variants || product.variants.length === 0) {
    return <p>Erreur : Aucune variante de produit trouv√©e.</p>;
  }

  const variant = product.variants[variantIndex];

  const nextVariant = () => {
    setVariantIndex((prev) => (prev + 1) % product.variants.length);
  };

  return (
  <div className="container mt-5">
  <div className="row justify-content-center">
    <div className="col-md-6">
      <Card className="text-center p-4 shadow-lg bg-light rounded-4" style={{ maxWidth: '400px', margin: 'auto' }}>
  <Card.Body>
    <Card.Title className="mb-3 fs-2 fw-bold text-primary">{variant.name}</Card.Title>
    <Card.Img
      variant="top"
      src={variant.image}
      alt={variant.name}
      className="mx-auto d-block"
      style={{ width: "180px", borderRadius: '12px', boxShadow: '0 4px 12px rgba(0,0,0,0.1)' }}
    />
    <Card.Text className="mt-3 text-secondary fs-5">{variant.description}</Card.Text>
    <h4 className="mt-4 text-success fw-semibold">{variant.price}</h4>
    <Button
      className="mt-4 px-5"
      variant="outline-primary"
      onClick={nextVariant}
      style={{ fontWeight: '600', letterSpacing: '0.05em' }}
      >
      Changer de mode
    </Button>
  </Card.Body>
</Card>


      <p className="text-center mt-4 fs-5">
        {firstName ? `Bonjour, ${firstName} !` : "Bonjour !"}
      </p>

      {firstName && (
        <div className="text-center">
          <img
            src="https://cdn-icons-png.flaticon.com/512/2922/2922561.png"
            alt="User"
            width="100"
            className="rounded-circle shadow-sm"
            style={{ marginTop: '10px' }}
          />
        </div>
      )}
    </div>
  </div>
</div>

  );
}

export default App;
