import React, { useState } from 'react';
import product from './product';
import 'bootstrap/dist/css/bootstrap.min.css';
import './app.css';  // <-- Import CSS ici
import { Card, Button } from 'react-bootstrap';

function App() {
  const [variantIndex, setVariantIndex] = useState(0);
  const [firstName] = useState("Phina");

  if (!product.variants || product.variants.length === 0) {
    return <p>Erreur : Aucune variante de produit trouv√©e.</p>;
  }

  const variant = product.variants[variantIndex];

  const nextVariant = () => {
    setVariantIndex((prev) => (prev + 1) % product.variants.length);
  };

  return (
    <div className="container mt-5">
      <div className="row justify-content-center">
        <div className="col-md-6">
          <Card className="card-product">
            <Card.Body>
              <Card.Title className="card-title">{variant.name}</Card.Title>
              <Card.Img
                variant="top"
                src={variant.image}
                alt={variant.name}
                className="mb-3"
              />
              <Card.Text className="card-text">{variant.description}</Card.Text>
              <h4 className="price">{variant.price}</h4>
              <Button
                className="btn-variant"
                variant="outline-primary"
                onClick={nextVariant}
              >
                Changer de mode
              </Button>
            </Card.Body>
          </Card>

          <p className="text-center mt-4 fs-5">
            {firstName ? `Bonjour, ${firstName} !` : "Bonjour !"}
          </p>

          {firstName && (
            <div className="text-center">
              <img
                src="https://cdn-icons-png.flaticon.com/512/2922/2922561.png"
                alt="User"
                width="100"
                className="rounded-circle shadow-sm"
                style={{ marginTop: '10px' }}
              />
            </div>
          )}
        </div>
      </div>
    </div>
  );
}

export default App;
