// src/App.js
import React, { useState } from 'react';
import product from './product';
import Name from './Name';
import Price from './Price';
import Description from './Description';
import Image from './Image';
import 'bootstrap/dist/css/bootstrap.min.css';
import { Card, Button } from 'react-bootstrap';

const firstName = "Phina"; // à changer selon ton besoin

function App() {
  const [imageIndex, setImageIndex] = useState(0);

  const nextImage = () => {
    setImageIndex((prevIndex) => (prevIndex + 1) % product.images.length);
  };

  return (
    <div className="container mt-4">
      <Card className="text-center p-3">
        <Name name={product.name} />
        <Image image={product.images[imageIndex]} name={product.name} />
        <Button variant="primary" className="m-2" onClick={nextImage}>Changer l'image</Button>
        <Price price={product.price} />
        <Description description={product.description} />
      </Card>
      <p className="text-center mt-3">
        {firstName ? `Bonjour, ${firstName} !` : "Bonjour !"}
      </p>
      {firstName && (
        <div className="text-center">
          <img
            src="https://cdn-icons-png.flaticon.com/512/2922/2922561.png"
            alt="User"
            width="100"
            style={{ marginTop: '10px' }}
          />
        </div>
      )}
    </div>
  );
}

export default App;
