import React from 'react';
import product from './product';
import Name from './Name';
import Price from './Price';
import Description from './Description';
import Image from './Image';

const firstName = "Ali"; // Remplace par ton prénom ou laisse vide pour tester la condition

function App() {
  return (
    <div style={{ maxWidth: "400px", margin: "auto", padding: "20px", textAlign: "center", background: "white", borderRadius: "10px", boxShadow: "0 0 10px rgba(0,0,0,0.1)" }}>
      <Image image={product.image} name={product.name} />
      <Name name={product.name} />
      <Price price={product.price} />
      <Description description={product.description} />

      <hr />
      <h3>Bonjour {firstName ? firstName : "!"}</h3>
      {firstName && <img src="https://i.pravatar.cc/100" alt="user" style={{ borderRadius: "50%" }} />}
    </div>
  );
}

export default App;
