import React, { useState } from 'react';

function Image({ image, alt = "product" }) {
  const [imgSrc, setImgSrc] = useState(image);

  const handleError = () => {
    setImgSrc("https://via.placeholder.com/400x300?text=Image+non+disponible");
  };

  return (
    <img
      src={imgSrc}
      alt={alt}
      onError={handleError}
      className="card-img-top"
      style={{ objectFit: "cover", height: "200px" }}
    />
  );
}

export default Image;
