import 'animate.css';
import React, { useState } from 'react';
import product from './product';
import 'bootstrap/dist/css/bootstrap.min.css';
import { Card, Button, Badge } from 'react-bootstrap';

function App() {
  const [variantIndex, setVariantIndex] = useState(0);
  const [firstName] = useState("Phina");

  if (!product.variants || product.variants.length === 0) {
    return <p>Erreur : Aucune variante de produit trouv√©e.</p>;
  }

  const variant = product.variants[variantIndex];

  const nextVariant = () => {
    setVariantIndex((prev) => (prev + 1) % product.variants.length);
  };

  return (
    <div className="d-flex justify-content-center align-items-center min-vh-100 bg-light px-3">
      <div className="w-100" style={{ maxWidth: '450px' }}>
        <Card key={variantIndex} className="text-center p-4 shadow-lg border-0 bg-white rounded-4 animate__animated animate__fadeIn">
          <Card.Body>
            <h6>
              <Badge bg="info" className="mb-2">Mode : {variant.mode}</Badge>
            </h6>
            <Card.Title className="mb-3 fs-3 fw-bold text-primary">{variant.name}</Card.Title>
            <Card.Img
              variant="top"
              src={variant.image}
              alt={variant.name}
              className="mx-auto d-block"
              style={{
                width: "160px",
                borderRadius: '20px',
                boxShadow: '0 8px 16px rgba(0,0,0,0.15)'
              }}
            />
            <Card.Text className="mt-3 text-secondary fs-6 px-2">{variant.description}</Card.Text>
            <h4 className="mt-4 text-success fw-semibold">{variant.price}</h4>
            <Button
              className="mt-4 px-4 py-2"
              variant="primary"
              onClick={nextVariant}
              style={{
                fontWeight: '600',
                letterSpacing: '0.05em',
                borderRadius: '30px'
              }}
            >
              Changer de mode
            </Button>
          </Card.Body>
        </Card>

        <div className="text-center mt-4">
          <p className="fs-5 text-dark">
            {firstName ? `Bonjour, ${firstName} !` : "Bonjour !"}
          </p>
          {firstName && (
            <img
              src="https://cdn-icons-png.flaticon.com/512/2922/2922561.png"
              alt="User"
              width="90"
              className="rounded-circle shadow-sm"
            />
          )}
        </div>
      </div>
    </div>
  );
}

export default App;
