import React, { useState } from 'react';
import products from "./product";
import Name from './Name';
import Price from './Price';
import Description from './Description';
import Image from './Image';
import 'bootstrap/dist/css/bootstrap.min.css';
import { Card, Container, Row, Col } from "react-bootstrap";
console.log(products); // Doit afficher un tableau avec 3 objets

const firstName = "Phina"; // à changer selon ton besoin

function App() {
 const nextImage = () => {
  setImageIndex((prevIndex) => (prevIndex + 1) % products[0].images.length); // ✅
  };

  return (
    <Container className="mt-5">
      <Row>
        {Array.isArray(products) && products.map((product, index) => (
          <Col key={index} md={4}>
            <Card className="mb-4 shadow-sm">
              <Image image={product.images[imageIndex]} />
              <Card.Body>
                <Name name={product.name} />
                <Price price={product.price} />
                <Description description={product.description} />
              </Card.Body>
            </Card>
          </Col>
        ))}
      </Row>
    </Container>
  );
}

export default App;
