// src/App.js
import React, { useState } from 'react';
import products from "./product";
import Name from './Name';
import Price from './Price';
import Description from './Description';
import Image from './Image';
import 'bootstrap/dist/css/bootstrap.min.css';
import { Card, Container, Row, Col, Button } from "react-bootstrap";

const firstName = "Phina"; // à changer selon ton besoin

function App() {
  // On crée un tableau d’index pour suivre l’image actuelle de chaque produit
  const [imageIndexes, setImageIndexes] = useState(products.map(() => 0));

  const nextImage = (productIndex) => {
    setImageIndexes((prevIndexes) => {
      const newIndexes = [...prevIndexes];
      const imagesLength = products[productIndex].images.length;
      newIndexes[productIndex] = (newIndexes[productIndex] + 1) % imagesLength;
      return newIndexes;
    });
  };
console.log('products:', products);
  return (
    <Container className="mt-5">
      <Row>
        {Array.isArray(products) && products.length > 0 ? (
  products.map((product, index) => (
    <Col key={index} md={4}>
      <Card className="mb-4 shadow-sm">
        <Image image={product.image} />
        <Card.Body>
          <Name name={product.name} />
          <Price price={product.price} />
          <Description description={product.description} />
        </Card.Body>
      </Card>
    </Col>
  ))
) : (
  <p>Aucun produit disponible</p>
)}
      </Row>
    </Container>
  );
}

export default App;
